import React, { useRef } from 'react';
import './VideoCard.css';


function VideoCard() {
    const [isVideoPlaying, setIsVideoPlaying] = 
    useState(false);
    const videoRef = useRef(null);

    const onVideoPress = () => {
        if (isVideoPlaying) {
            // stop
            videoRef.current.pause()
            setIsVideoPlaying(false)
        } else {
            // play
            videoRef.current.play()
            setIsVideoPlaying(true)
        }
    }

    return (
        <div className='videoCard'>
            <video 
            ref={videoRef}
            onClick={onVideoPress}
            className="videoCard__player"
            src='https://scontent-ssn1-1.cdninstagram.com/v/t50.2886-16/117932784_329931645035937_7460453733854060462_n.mp4?_nc_ht=scontent-ssn1-1.cdninstagram.com&_nc_cat=109&_nc_ohc=Tn6WhLZ7rgMAX_vE_el&oe=5F88B53A&oh=bbc6d5ecf8da54937f31c2a20a668384'
            alt='TG reel video'
            loop={true}
            />

        </div>
    )
}

export default VideoCard
